//check proxmox nodes API

try {
    var resultProxmoxPraser = {};
    var req = new HttpRequest();
    var resultProxmoxGet;
    req.addHeader('Content-Type: application/json');
    req.addHeader("Authorization: PVEAPIToken={$PROXMOX_API_KEY}={$PROXMOX_API_SECRET}");
    resultProxmoxGet = req.get("{$PROXMOX_API_HOST}/api2/json/nodes", "" );

    if (req.getStatus() != 200) {
        throw 'Response code: '+req.getStatus();
    }

    resultProxmoxPraser = JSON.parse(resultProxmoxGet);
    resultProxmoxPraser = resultProxmoxPraser.data;

    var resultProxmox = {};
    for(var i in resultProxmoxPraser) {
        resultProxmox[resultProxmoxPraser[i].node] = { id: resultProxmoxPraser[i].id, type: resultProxmoxPraser[i].type, node: resultProxmoxPraser[i].node, status: resultProxmoxPraser[i].status, uptime: resultProxmoxPraser[i].uptime };
    }
} catch (error) {
    Zabbix.Log(4, 'proxmox praser error : '+error);
    var resultProxmoxPraser = {};
    var resultProxmox = {};
}

