    Zabbix.Log(4, 'proxmox json data empty : '+error);
    ProxmoxStatusNode = 'no-proxmox-host';
}

    result[count] = { wwn: dataValue[i].device.wwn, serial_number: dataValue[i].device.serial_number, host_id: dataValue[i].device.host_id, UpdatedAt: lastUpdatedAt, rotational_speed: dataValue[i].device.rotational_speed, device_name: dataValue[i].device.device_name, device_serial_id: dataValue[i].device.device_serial_id, model_name: dataValue[i].device.model_name, device_status: dataValue[i].device.device_status, power_on_hours: dataValue[i].smart.power_on_hours, temp: dataValue[i].smart.temp, CreatedAt: lastCreatedAt, timeFromCreateToUpdate: timeFromCreateToUpdate, timePastFromLastUpdate: timePastFromLastUpdate, proxmoxStatus: ProxmoxStatusNode }
